@use "../util/" as *;

.hero {
    div {
        width: 100%;
    }

    h1 {
        font-size: 1.75em;
        color: var(--titleColor);
        margin-block: 0.25em;

        @include breakpoint(medium) {
            font-size: 2.25em;
        }

        @include breakpoint(large) {
            font-size: 2.625em;
        }
    }

    h3 {
        font-size: 1em;
        color: var(--subtitleColor);

        @include breakpoint(medium) {
            font-size: 1.25em;
        }
    }

    p {
        color: var(--textColor);
        margin-block: 1em;

        @include breakpoint(medium) {
            margin-bottom: 3em;
        }
    }

    .mouse {
        margin-top: 2em;
        display: inline-block;

        .scroll {
            animation: scrollAnim 1s infinite alternate-reverse;
        }

        @keyframes scrollAnim {
            from {
                transform: translate(262px, 798px);
            }
            to {
                transform: translate(262px, 830px);
            }
        }
    }
}

.fadein {
    position: relative;
    animation: fadeAnim 2s ease;
}

@keyframes fadeAnim {
    from {
        opacity: 0;
        transform: translateY(60px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

sources.scss\\layout\\_about.scss:
@use "../abstracts" as *;
@use "../util" as *;

.about {
    @include breakpoint(medium) {
        flex-direction: row;
    }

    .col-left {
        width: 100%;

        ul {
            list-style-type: none;
            display: grid;
            grid-template-columns: 1fr 1fr 1fr;
            gap: 0 0.625em;
            color: var(--textColor);
            font-size: 0.8125em;

            li {
                margin: 1em 1em 0 0;

                &::before {

                    color: var(--subtitleColor);
                    padding-right: 0.5em;
                }
            }
        }
        @media  (max-width:700px) {
            ul{
                 grid-template-columns: 1fr 1fr;
            }
        }
    }

    .col-right {
        margin-block: 3em;
        @include widthHeight(200px, 200px);

        @include breakpoint(medium) {
            @include widthHeight(400px, 400px);
            padding: 2em;
        }

        .about-image {
            position: relative;

            @include breakpoint(medium) {
                top: 50px;
            }

            &::after {
                content: " ";
                position: absolute;
                left: 20px;
                top: 20px;
                @include widthHeight(100%, 100%);
                border: 3px solid var(--subtitleColor);
                z-index: -1;
            }

            &::after,
            img {
                border-radius: 5px;
            }
        }
    }
}

